import { Component, Input } from '@angular/core';

import { CoreTagFeedElement } from '@features/tag/services/tag-helper';

/**
 * Component to render a tag area that uses the "core_tag/tagfeed" web template.
 */
@Component({
    selector: 'core-tag-feed',
    templateUrl: 'core-tag-feed.html',
})
export class CoreTagFeedComponent {

    @Input() items?: CoreTagFeedElement[]; // Area items to render.

}
<ion-card *ngFor="let item of items">
    <ion-item class="ion-text-wrap" [href]="item.url" core-link [capture]="true">
        <core-user-avatar *ngIf="item.avatarUrl" [profileUrl]="item.avatarUrl" slot="start" [linkProfile]="false" />

        <core-mod-icon *ngIf="item.iconUrl" [modicon]="item.iconUrl" slot="start" [showAlt]="false" />
        <ion-label>
            <p class="item-heading">{{ item.heading }}</p>
            <p *ngFor="let text of item.details">{{ text }}</p>
        </ion-label>
    </ion-item>
</ion-card>
