import { Component } from '@angular/core';
import { CoreBlockPreRenderedComponent } from '@features/block/components/pre-rendered-block/pre-rendered-block';

/**
 * Component to render a badges block.
 */
@Component({
    selector: 'addon-block-badges',
    templateUrl: '../../../../../core/features/block/components/pre-rendered-block/core-block-pre-rendered.html',
    styleUrls: ['badges.scss'],
})
export class AddonBlockBadgesComponent extends CoreBlockPreRenderedComponent {}
<ion-item-divider class="ion-text-wrap" *ngIf="title" [sticky]="true">
    <ion-label>
        <h2>
            <core-format-text [text]="title | translate" contextLevel="block" [contextInstanceId]="block.instanceid"
                [courseId]="courseId" />
        </h2>
    </ion-label>
</ion-item-divider>
<core-loading [hideUntil]="loaded" [fullscreen]="false">
    <ion-item *ngIf="block.contents?.content" class="ion-text-wrap core-block-content">
        <ion-label>
            <core-format-text [text]="block.contents?.content" contextLevel="block" [contextInstanceId]="block.instanceid"
                [courseId]="courseId" />
        </ion-label>
    </ion-item>
    <ion-item *ngIf="block.contents?.footer" class="ion-text-wrap core-block-footer">
        <ion-label>
            <core-format-text [text]="block.contents?.footer" contextLevel="block" [contextInstanceId]="block.instanceid"
                [courseId]="courseId" />
        </ion-label>
    </ion-item>
</core-loading>
