import { Component, OnInit } from '@angular/core';
import {
    CoreCourseModuleSummaryResult,
    CoreCourseModuleSummaryComponent,
} from '@features/course/components/module-summary/module-summary';
import { CoreCourse } from '@features/course/services/course';
import { CoreCourseHelper, CoreCourseModuleData, CoreCourseSection } from '@features/course/services/course-helper';
import { CoreCourseModuleDelegate } from '@features/course/services/module-delegate';
import { CoreNavigator } from '@services/navigator';
import { CoreSites } from '@services/sites';
import { CoreDomUtils } from '@services/utils/dom';
import { CoreUtils } from '@services/utils/utils';

/**
 * Page that displays a module preview.
 */
@Component({
    selector: 'page-core-course-module-preview',
    templateUrl: 'module-preview.html',
    styleUrls: ['module-preview.scss'],
})
export class CoreCourseModulePreviewPage implements OnInit {

    title!: string;
    module!: CoreCourseModuleData;
    section?: CoreCourseSection; // The section the module belongs to.
    courseId!: number;
    loaded = false;
    unsupported = false;
    isDisabledInSite = false;
    showManualCompletion = false;
    displayOpenInBrowser = false;

    protected debouncedUpdateModule?: () => void; // Update the module after a certain time.

    /**
     * @inheritdoc
     */
    async ngOnInit(): Promise<void> {
        try {
            this.module = CoreNavigator.getRequiredRouteParam<CoreCourseModuleData>('module');
            this.courseId = CoreNavigator.getRequiredRouteNumberParam('courseId');
            this.section = CoreNavigator.getRouteParam<CoreCourseSection>('section');
        } catch (error) {
            CoreDomUtils.showErrorModal(error);

            CoreNavigator.back();

            return;
        }

        this.displayOpenInBrowser = !!CoreSites.getCurrentSite()?.shouldDisplayInformativeLinks();
        this.debouncedUpdateModule = CoreUtils.debounce(() => {
            this.doRefresh();
        }, 10000);

        await this.fetchModule();
    }

    /**
     * Fetch module.
     *
     * @returns Promise resolved when done.
     */
    protected async fetchModule(refresh = false): Promise<void> {
        if (refresh) {
            this.module = await CoreCourse.getModule(this.module.id, this.courseId);
        }

        await CoreCourseHelper.loadModuleOfflineCompletion(this.courseId, this.module);

        this.unsupported = !CoreCourseModuleDelegate.getHandlerName(this.module.modname);
        if (!this.unsupported) {
            this.module.handlerData =
                await CoreCourseModuleDelegate.getModuleDataFor(this.module.modname, this.module, this.courseId);
        } else {
            this.isDisabledInSite = CoreCourseModuleDelegate.isModuleDisabledInSite(this.module.modname);
        }

        this.title = this.module.name;

        this.showManualCompletion = await CoreCourseModuleDelegate.manualCompletionAlwaysShown(this.module);

        this.loaded = true;
    }

    /**
     * Opens a module summary page.
     */
    async openModuleSummary(): Promise<void> {
        if (!this.module) {
            return;
        }

        const data = await CoreDomUtils.openSideModal<CoreCourseModuleSummaryResult>({
            component: CoreCourseModuleSummaryComponent,
            componentProps: {
                moduleId: this.module.id,
                module: this.module,
                description: this.module.description,
                component: this.module.modname,
                courseId: this.courseId,
                displayOptions: {
                    displayDescription: false,
                    displayBlog: false,
                },
            },
        });

        if (data) {
            if (this.loaded && data.action == 'refresh') {
                this.loaded = false;
                try {
                    await this.doRefresh(undefined);
                } finally {
                    this.loaded = true;
                }
            }
        }
    }

    /**
     * Refresh the data.
     *
     * @param refresher Refresher.
     * @returns Promise resolved when done.
     */
    async doRefresh(refresher?: HTMLIonRefresherElement): Promise<void> {

        await CoreCourse.invalidateModule(this.module.id);

        this.fetchModule(true);

        refresher?.complete();
    }

    /**
     * The completion of the modules has changed.
     *
     * @returns Promise resolved when done.
     */
    async onCompletionChange(): Promise<void> {
        // Update the module data after a while.
        this.debouncedUpdateModule?.();
    }

}
<ion-header collapsible>
    <ion-toolbar>
        <ion-buttons slot="start">
            <ion-back-button [text]="'core.back' | translate" />
        </ion-buttons>
        <ion-title>
            <h1>
                <core-format-text [text]="title" contextLevel="module" [contextInstanceId]="module.id" [courseId]="courseId" />
            </h1>
        </ion-title>

        <ion-buttons slot="end">
            <ion-button fill="clear" *ngIf="displayOpenInBrowser && module.url && module.uservisible && !unsupported" [href]="module.url"
                core-link [showBrowserWarning]="false" [attr.aria-label]="'core.openinbrowser' | translate">
                <ion-icon name="fas-up-right-from-square" slot="icon-only" aria-hidden="true" />
            </ion-button>
            <ion-button fill="clear" (click)="openModuleSummary()" aria-haspopup="true" [attr.aria-label]="'core.info' | translate">
                <ion-icon name="fas-circle-info" slot="icon-only" aria-hidden="true" />
            </ion-button>
        </ion-buttons>
    </ion-toolbar>
</ion-header>
<ion-content class="limited-width">
    <ion-refresher slot="fixed" [disabled]="!loaded" (ionRefresh)="doRefresh($event.target)">
        <ion-refresher-content pullingText="{{ 'core.pulltorefresh' | translate }}" />
    </ion-refresher>
    <core-loading [hideUntil]="loaded" class="has-spacer">
        <core-course-module-info [module]="module" [courseId]="courseId" [description]="module.description" [component]="module.modname"
            [componentId]="module.id" [expandDescription]="true" [showAvailabilityInfo]="true" (completionChanged)="onCompletionChange()"
            [showManualCompletion]="showManualCompletion">

            <div class="core-module-additional-info">
                <p *ngIf="module.handlerData?.extraBadge" class="ion-text-wrap" [innerHTML]="module.handlerData?.extraBadge">
                </p>

                <!-- Hidden badges -->
                <ion-badge color="secondary" *ngIf="module.visible === 0" class="ion-text-wrap">
                    <ion-icon name="far-eye-slash" aria-hidden="true" />
                    {{ 'core.course.hiddenfromstudents' | translate }}
                </ion-badge>
                <ion-badge color="secondary" *ngIf="module.visible !== 0 && module.isStealth" class="ion-text-wrap">
                    <ion-icon name="fas-eye-low-vision" aria-hidden="true" />
                    {{ 'core.course.hiddenoncoursepage' | translate }}
                </ion-badge>
            </div>
        </core-course-module-info>

        <core-course-unsupported-module *ngIf="unsupported" [module]="module" />
    </core-loading>

    <core-course-module-navigation collapsible-footer appearOnBottom [hidden]="!loaded" [courseId]="courseId"
        [currentModuleId]="module.id" />
</ion-content>
