import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { CoreCourse } from '@features/course/services/course';
import { CoreCourseModuleCompletionData, CoreCourseModuleData } from '@features/course/services/course-helper';
import { CoreCourseModuleDelegate } from '@features/course/services/module-delegate';
import { CoreSites } from '@services/sites';

/**
 * Display info about a module:
 *
 * Description:
 * Module descriptions are shortened by default, allowing the user to see the full description by clicking in it.
 *
 * Completion dates, status and buttons.
 *
 * You can add also add custom information that will be included at the end.
 */
@Component({
    selector: 'core-course-module-info',
    templateUrl: 'core-course-module-info.html',
    styleUrls: ['course-module-info.scss'],
})
export class CoreCourseModuleInfoComponent implements OnInit {

    @Input() module!: CoreCourseModuleData; // The module to render.
    @Input() courseId!: number; // The courseId the module belongs to.

    @Input() component!: string; // Component for format text directive.
    @Input() componentId!: string | number; // Component ID to use in conjunction with the component.

    @Input() description?: string | false; // The description to display. If false, no description will be shown.
    @Input() expandDescription = false; // If the description should be expanded by default.

    @Input() showAvailabilityInfo = false; // If show availability info on the box.

    @Input() hasDataToSync = false; // If the activity has any data to be synced.

    @Input() showManualCompletion = true; // Whether to show manual completion, true by default.
    @Output() completionChanged = new EventEmitter<CoreCourseModuleCompletionData>(); // Notify when completion changes.

    modicon = '';
    showCompletion = false; // Whether to show completion.
    moduleNameTranslated = '';

    /**
     * @inheritdoc
     */
    async ngOnInit(): Promise<void> {
        this.modicon = await CoreCourseModuleDelegate.getModuleIconSrc(this.module.modname, this.module.modicon, this.module);

        this.moduleNameTranslated = CoreCourse.translateModuleName(this.module.modname, this.module.modplural);
        this.showCompletion = CoreSites.getRequiredCurrentSite().isVersionGreaterEqualThan('3.11');
    }

}
<ion-item class="ion-text-wrap" collapsible>
    <core-mod-icon slot="start" [modicon]="modicon" [modname]="module.modname" [componentId]="module.instance" />
    <ion-label>
        <h1>
            <core-format-text [text]="module.name" contextLevel="module" [component]="component" [componentId]="componentId"
                [contextInstanceId]="module.id" [courseId]="courseId" />
            <ion-icon name="fas-lock" *ngIf="module.visible === 0 || module.uservisible === false"
                [attr.aria-label]="'core.restricted' | translate" />
        </h1>
        <ng-content select="[title]"></ng-content>
    </ion-label>
</ion-item>

<div class="core-module-info-box">
    <!-- Module completion. -->
    <div class="core-module-info-completion core-module-info-box-section" *ngIf="showCompletion &&
            module.completiondata && (module.completiondata.isautomatic || (showManualCompletion && module.uservisible))">
        <core-course-module-completion [completion]="module.completiondata" [moduleName]="module.name" [moduleId]="module.id"
            [showCompletionConditions]="true" [showManualCompletion]="showManualCompletion && module.uservisible"
            (completionChanged)="completionChanged.emit($event)" />
    </div>

    <!-- Activity dates. -->
    <div *ngIf="module.dates && module.dates.length" class="core-module-dates core-module-info-box-section">
        <core-reminders-date *ngFor="let date of module.dates" [component]="component" [instanceId]="module.id" [type]="date.dataid"
            [label]="date.label" [time]="date.timestamp" [relativeTo]="date.relativeto" [title]="module.name" [url]="module.url" />
    </div>

    <!-- Availability info space. -->
    <div class="core-module-availabilityinfo core-module-info-box-section" *ngIf="showAvailabilityInfo && module.availabilityinfo">
        <ion-icon name="fas-lock" [attr.aria-label]="'core.restricted' | translate" />
        <core-format-text [text]="module.availabilityinfo" contextLevel="module" [contextInstanceId]="module.id"
            [courseId]="module.course" />
    </div>

    <div class="core-module-info-description core-module-info-box-section" *ngIf="description">
        <core-format-text [text]="description" [component]="component" [componentId]="componentId" contextLevel="module"
            [contextInstanceId]="module.id" [courseId]="courseId" [collapsible-item]="expandDescription ? null : ''" />
    </div>
    <ng-content select="[description]"></ng-content>
</div>

<ng-content></ng-content>

<!-- Activity has something offline. -->
<ion-card class="core-warning-card" *ngIf="hasDataToSync">
    <ion-item>
        <ion-icon name="fas-triangle-exclamation" slot="start" aria-hidden="true" />
        <ion-label>{{ 'core.hasdatatosync' | translate: {$a: moduleNameTranslated} }}</ion-label>
    </ion-item>
</ion-card>
