import { Component, Input, Output, EventEmitter, OnChanges } from '@angular/core';
import { CoreSearchGlobalSearchResult, CoreSearchGlobalSearchResultContext } from '@features/search/services/global-search';

@Component({
    selector: 'core-search-global-search-result',
    templateUrl: 'global-search-result.html',
    styleUrls: ['./global-search-result.scss'],
})
export class CoreSearchGlobalSearchResultComponent implements OnChanges {

    @Input() result!: CoreSearchGlobalSearchResult;
    @Input() showCourse?: boolean;

    renderedContext: CoreSearchGlobalSearchResultContext | null = null;
    renderedIcon: string | null = null;

    @Output() onClick = new EventEmitter();

    /**
     * @inheritdoc
     */
    ngOnChanges(): void {
        this.renderedContext = this.computeRenderedContext();
        this.renderedIcon = this.computeRenderedIcon();
    }

    /**
     * Calculate the value of the context to render.
     *
     * @returns Rendered context.
     */
    private computeRenderedContext(): CoreSearchGlobalSearchResultContext | null {
        const context = { ...this.result.context } ?? {};

        if (this.showCourse === false) {
            delete context.courseName;
        }

        return Object.keys(context).length > 0 ? context : null;
    }

    /**
     * Calculate the value of the icon to render.
     *
     * @returns Rendered icon.
     */
    private computeRenderedIcon(): string | null {
        return this.result.module?.name === 'forum'  && this.result.module.area === 'post'
            ? 'fa-message'
            : null;
    }

}
<ion-item lines="inset" button (click)="onClick.emit($event)">
    <core-course-image *ngIf="result.course" [course]="result.course" />
    <core-user-avatar *ngIf="result.user" [user]="result.user" [linkProfile]="false" />
    <ion-label>
        <h3 *ngIf="result.title">
            <ion-icon *ngIf="renderedIcon" [name]="renderedIcon" aria-hidden="true" />
            <core-mod-icon *ngIf="!renderedIcon && result.module" [modicon]="result.module.iconurl"
                [modname]="result.module.name" />
            <img *ngIf="!renderedIcon && !result.module && result.component" [src]="result.component.iconurl" alt="" class="result-icon"
                core-external-content [component]="result.component.name">
            <core-format-text [text]="result.title" />
        </h3>
        <core-format-text *ngIf="result.content && !result.course && !result.user" [text]="result.content" />
        <div *ngIf="renderedContext" class="result-context-wrapper">
            <div *ngIf="renderedContext.courseName" class="result-context">
                <ion-icon name="fas-graduation-cap" aria-hidden="true" />
                <core-format-text [text]="renderedContext.courseName" />
            </div>
            <div *ngIf="renderedContext.userName" class="result-context">
                <ion-icon name="fas-user" aria-hidden="true" />
                <span>{{ 'core.search.resultby' | translate: { $a: renderedContext.userName } }}</span>
            </div>
        </div>
    </ion-label>
</ion-item>
