import { Component, Input } from '@angular/core';

import { CoreUserTagFeedElement } from '@features/user/services/handlers/tag-area';

/**
 * Component to render the user tag area.
 */
@Component({
    selector: 'core-user-tag-area',
    templateUrl: 'core-user-tag-area.html',
})
export class CoreUserTagAreaComponent {

    @Input() items?: CoreUserTagFeedElement[]; // Area items to render.

}
<ion-card *ngFor="let item of items">
    <ion-item class="ion-text-wrap" core-user-link [userId]="item.user.id">
        <core-user-avatar [user]="item.user" slot="start" [linkProfile]="false" />
        <ion-label>
            <p class="item-heading">{{ item.heading }}</p>
        </ion-label>
    </ion-item>

</ion-card>
