import { Component, EventEmitter, Input, Output, ViewChild } from '@angular/core';
import { Translate } from '@singletons';
import { ModalOptions } from '@ionic/core';
import { CoreDomUtils } from '@services/utils/dom';
import { IonSelect } from '@ionic/angular';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';

/**
 * Component that show a combo select button (combobox).
 *
 * @description
 *
 * Example using modal:
 *
 * <core-combobox interface="modal" (onChange)="selectedChanged($event)" [modalOptions]="modalOptions"
 *      icon="fas-folder" [label]="'core.course.section' | translate">
 *      <span slot="text">selection</span>
 * </core-combobox>
 *
 * Example using popover:
 *
 * <core-combobox [label]="'core.show' | translate" [selection]="selectedFilter" (onChange)="selectedChanged()">
 *      <ion-select-option value="1">1</ion-select-option>
 * </core-combobox>
 */
@Component({
    selector: 'core-combobox',
    templateUrl: 'core-combobox.html',
    styleUrls: ['combobox.scss'],
    providers: [
        {
            provide: NG_VALUE_ACCESSOR,
            multi:true,
            useExisting: CoreComboboxComponent,
        },
    ],
})
export class CoreComboboxComponent implements ControlValueAccessor {

    @ViewChild(IonSelect) select!: IonSelect;

    @Input() interface: 'popover' | 'modal' = 'popover';
    @Input() label = Translate.instant('core.show'); // Aria label.
    @Input() disabled = false;
    @Input() selection = '';
    @Output() onChange = new EventEmitter<unknown>(); // Will emit an event the value changed.

    // Additional options when interface modal is selected.
    @Input() icon?: string; // Icon for modal interface.
    @Input() modalOptions?: ModalOptions; // Will emit an event the value changed.
    @Input() listboxId = '';

    expanded = false;

    protected touched = false;
    protected formOnChange?: (value: unknown) => void;
    protected formOnTouched?: () => void;

    /**
     * @inheritdoc
     */
    writeValue(selection: string): void {
        this.selection = selection;
    }

    /**
     * @inheritdoc
     */
    registerOnChange(onChange: (value: unknown) => void): void {
        this.formOnChange = onChange;
    }

    /**
     * @inheritdoc
     */
    registerOnTouched(onTouched: () => void): void {
        this.formOnTouched = onTouched;
    }

    /**
     * @inheritdoc
     */
    setDisabledState(disabled: boolean): void {
        this.disabled = disabled;
    }

    /**
     * Callback when the selected value changes.
     *
     * @param selection Selected value.
     */
    onValueChanged(selection: unknown): void {
        this.touch();
        this.onChange.emit(selection);
        this.formOnChange?.(selection);
    }

    /**
     * Shows combobox modal.
     *
     * @param event Event.
     * @returns Promise resolved when done.
     */
    async openSelect(event?: UIEvent): Promise<void> {
        this.touch();

        if (this.interface == 'modal') {
            if (this.expanded || !this.modalOptions) {
                return;
            }
            this.expanded = true;

            if (this.listboxId) {
                this.modalOptions.id = this.listboxId;
            }

            const data = await CoreDomUtils.openModal(this.modalOptions);
            this.expanded = false;

            if (data) {
                this.onValueChanged(data);
            }
        } else if (this.select) {
            this.select.open(event);
        }
    }

    /**
     * Mark as touched.
     */
    protected touch(): void {
        if (this.touched) {
            return;
        }

        this.touched = true;
        this.formOnTouched?.();
    }

}
<ion-button (click)="openSelect($event)" *ngIf="interface !== 'modal' && icon" [disabled]="disabled">
    <ion-icon [name]="icon" [attr.aria-label]="label" slot="start" />
    <div class="select-icon" role="presentation" aria-hidden="true">
        <div class="select-icon-inner"></div>
    </div>
</ion-button>
<ion-select *ngIf="interface !== 'modal'" class="ion-text-start" [(ngModel)]="selection" (ngModelChange)="onValueChanged(selection)"
    [interface]="interface" [attr.aria-label]="label + ': ' + selection" [disabled]="disabled" [hidden]="!!icon">
    <ng-content></ng-content>
</ion-select>

<ion-button *ngIf="interface === 'modal'" aria-haspopup="listbox" [attr.aria-controls]="listboxId" [attr.aria-owns]="listboxId"
    [attr.aria-expanded]="expanded" (click)="openSelect()" [disabled]="disabled" expand="block" role="combobox">
    <ion-icon *ngIf="icon" [name]="icon" slot="start" aria-hidden="true" />
    <span class="sr-only" *ngIf="label">{{ label }}:</span>
    <div class="select-text">
        <slot name="text">{{selection}}</slot>
    </div>
    <div class="select-icon" role="presentation" aria-hidden="true">
        <div class="select-icon-inner"></div>
    </div>
</ion-button>
