import { Component, OnDestroy, OnInit } from '@angular/core';
import { CoreTimeUtils } from '@services/utils/time';
import { CoreDomUtils } from '@services/utils/dom';
import { CoreSites } from '@services/sites';
import { CoreUser, CoreUserProfile } from '@features/user/services/user';
import { AddonBadges, AddonBadgesUserBadge } from '../../services/badges';
import { CoreUtils } from '@services/utils/utils';
import { CoreCourses, CoreEnrolledCourseData } from '@features/courses/services/courses';
import { CoreNavigator } from '@services/navigator';
import { ActivatedRoute } from '@angular/router';
import { CoreSwipeNavigationItemsManager } from '@classes/items-management/swipe-navigation-items-manager';
import { AddonBadgesUserBadgesSource } from '@addons/badges/classes/user-badges-source';
import { CoreRoutedItemsManagerSourcesTracker } from '@classes/items-management/routed-items-manager-sources-tracker';
import { CoreAnalytics, CoreAnalyticsEventType } from '@services/analytics';
import { CoreTime } from '@singletons/time';

/**
 * Page that displays the list of calendar events.
 */
@Component({
    selector: 'page-addon-badges-issued-badge',
    templateUrl: 'issued-badge.html',
})
export class AddonBadgesIssuedBadgePage implements OnInit, OnDestroy {

    protected badgeHash = '';
    protected userId!: number;
    protected logView: (badge: AddonBadgesUserBadge) => void;

    courseId = 0;
    user?: CoreUserProfile;
    course?: CoreEnrolledCourseData;
    badge?: AddonBadgesUserBadge;
    badges: CoreSwipeNavigationItemsManager;
    badgeLoaded = false;
    currentTime = 0;

    constructor(protected route: ActivatedRoute) {
        this.courseId = CoreNavigator.getRouteNumberParam('courseId') || this.courseId; // Use 0 for site badges.
        this.userId = CoreNavigator.getRouteNumberParam('userId') || CoreSites.getRequiredCurrentSite().getUserId();
        this.badgeHash = CoreNavigator.getRouteParam('badgeHash') || '';

        const source = CoreRoutedItemsManagerSourcesTracker.getOrCreateSource(
            AddonBadgesUserBadgesSource,
            [this.courseId, this.userId],
        );

        this.badges = new CoreSwipeNavigationItemsManager(source);

        this.logView = CoreTime.once((badge) => {
            CoreAnalytics.logEvent({
                type: CoreAnalyticsEventType.VIEW_ITEM,
                ws: 'core_badges_view_user_badges',
                name: badge.name,
                data: { id: badge.uniquehash, category: 'badges' },
                url: `/badges/badge.php?hash=${badge.uniquehash}`,
            });
        });
    }

    /**
     * View loaded.
     */
    ngOnInit(): void {
        this.fetchIssuedBadge().finally(() => {
            this.badgeLoaded = true;
        });

        this.badges.start();
    }

    /**
     * @inheritdoc
     */
    ngOnDestroy(): void {
        this.badges.destroy();
    }

    /**
     * Fetch the issued badge required for the view.
     *
     * @returns Promise resolved when done.
     */
    async fetchIssuedBadge(): Promise<void> {
        this.currentTime = CoreTimeUtils.timestamp();

        this.user = await CoreUser.getProfile(this.userId, this.courseId, true);

        try {
            const badges = await AddonBadges.getUserBadges(this.courseId, this.userId);
            const badge = badges.find((badge) => this.badgeHash == badge.uniquehash);

            if (!badge) {
                return;
            }

            this.badge = badge;
            if (badge.courseid) {
                try {
                    this.course = await CoreCourses.getUserCourse(badge.courseid, true);
                } catch {
                    // Maybe an old deleted course.
                    this.course = undefined;
                }
            }

            this.logView(badge);
        } catch (message) {
            CoreDomUtils.showErrorModalDefault(message, 'Error getting badge data.');
        }
    }

    /**
     * Refresh the badges.
     *
     * @param refresher Refresher.
     */
    async refreshBadges(refresher?: HTMLIonRefresherElement): Promise<void> {
        await CoreUtils.ignoreErrors(Promise.all([
            AddonBadges.invalidateUserBadges(this.courseId, this.userId),
        ]));

        await CoreUtils.ignoreErrors(Promise.all([
            this.fetchIssuedBadge(),
        ]));

        refresher?.complete();
    }

}
<ion-header>
    <ion-toolbar>
        <ion-buttons slot="start">
            <ion-back-button [text]="'core.back' | translate" />
        </ion-buttons>
        <ion-title>
            <h1 *ngIf="badge">{{ badge.name }}</h1>
            <h1 *ngIf="!badge">{{ 'addon.badges.badges' | translate }}</h1>
        </ion-title>
    </ion-toolbar>
</ion-header>
<ion-content [core-swipe-navigation]="badges" class="limited-width">
    <ion-refresher slot="fixed" [disabled]="!badgeLoaded" (ionRefresh)="refreshBadges($event.target)">
        <ion-refresher-content pullingText="{{ 'core.pulltorefresh' | translate }}" />
    </ion-refresher>
    <core-loading [hideUntil]="badgeLoaded">
        <ion-item-group *ngIf="badge">
            <ion-item class="ion-text-wrap ion-text-center">
                <ion-label>
                    <img *ngIf="badge.badgeurl" class="large-avatar" [src]="badge.badgeurl" core-external-content [alt]="badge.name" />
                    <ion-badge color="danger" *ngIf="badge.dateexpire && currentTime >= badge.dateexpire">
                        {{ 'addon.badges.expired' | translate }}
                    </ion-badge>
                </ion-label>
            </ion-item>
        </ion-item-group>

        <ion-item-group *ngIf="user">
            <ion-item-divider>
                <ion-label>
                    <h2>{{ 'addon.badges.recipientdetails' | translate}}</h2>
                </ion-label>
            </ion-item-divider>
            <ion-item class="ion-text-wrap">
                <ion-label>
                    <p class="item-heading">{{ 'core.name' | translate}}</p>
                    <p>{{ user.fullname }}</p>
                </ion-label>
            </ion-item>
        </ion-item-group>

        <ng-container *ngIf="badge">
            <ion-item-group>
                <ion-item-divider>
                    <ion-label>
                        <h2>{{ 'addon.badges.issuerdetails' | translate}}</h2>
                    </ion-label>
                </ion-item-divider>
                <ion-item class="ion-text-wrap" *ngIf="badge.issuername">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.issuername' | translate}}</p>
                        <p>{{ badge.issuername }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.issuercontact">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.contact' | translate}}</p>
                        <p><a href="mailto:{{badge.issuercontact}}" core-link auto-login="no" [showBrowserWarning]="false">
                                {{ badge.issuercontact }}
                            </a></p>
                    </ion-label>
                </ion-item>
            </ion-item-group>

            <ion-item-group>
                <ion-item-divider>
                    <ion-label>
                        <h2>{{ 'addon.badges.badgedetails' | translate}}</h2>
                    </ion-label>
                </ion-item-divider>
                <ion-item class="ion-text-wrap" *ngIf="badge.name">
                    <ion-label>
                        <p class="item-heading">{{ 'core.name' | translate}}</p>
                        <p>{{ badge.name }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.version">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.version' | translate}}</p>
                        <p>{{ badge.version }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.language">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.language' | translate}}</p>
                        <p>{{ badge.language }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.description">
                    <ion-label>
                        <p class="item-heading">{{ 'core.description' | translate}}</p>
                        <p>{{ badge.description }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.imageauthorname">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.imageauthorname' | translate}}</p>
                        <p>{{ badge.imageauthorname }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.imageauthoremail">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.imageauthoremail' | translate}}</p>
                        <p><a href="mailto:{{badge.imageauthoremail}}" core-link auto-login="no" [showBrowserWarning]="false">
                                {{ badge.imageauthoremail }}
                            </a></p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.imageauthorurl">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.imageauthorurl' | translate}}</p>
                        <p><a [href]="badge.imageauthorurl" core-link auto-login="no"> {{ badge.imageauthorurl }} </a></p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.imagecaption">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.imagecaption' | translate}}</p>
                        <p>{{ badge.imagecaption }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="course">
                    <ion-label>
                        <p class="item-heading">{{ 'core.course' | translate}}</p>
                        <p>
                            <core-format-text [text]="course.fullname" contextLevel="course" [contextInstanceId]="courseId" />
                        </p>
                    </ion-label>
                </ion-item>
                <!-- Criteria (not yet available) -->
            </ion-item-group>

            <ion-item-group>
                <ion-item-divider>
                    <ion-label>
                        <h2>{{ 'addon.badges.issuancedetails' | translate}}</h2>
                    </ion-label>
                </ion-item-divider>
                <ion-item class="ion-text-wrap" *ngIf="badge.dateissued">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.dateawarded' | translate}}</p>
                        <p>{{badge.dateissued * 1000 | coreFormatDate }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.dateexpire">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.expirydate' | translate}}</p>
                        <p>
                            {{ badge.dateexpire * 1000 | coreFormatDate }}
                            <span class="text-danger" *ngIf="currentTime >= badge.dateexpire">
                                {{ 'addon.badges.warnexpired' | translate }}
                            </span>
                        </p>
                    </ion-label>
                </ion-item>
                <!-- Evidence (not yet available) -->
            </ion-item-group>

            <!-- Endorsement -->
            <ion-item-group *ngIf="badge.endorsement">
                <ion-item-divider>
                    <ion-label>
                        <h2>{{ 'addon.badges.bendorsement' | translate}}</h2>
                    </ion-label>
                </ion-item-divider>
                <ion-item class="ion-text-wrap" *ngIf="badge.endorsement.issuername">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.issuername' | translate}}</p>
                        <p>{{ badge.endorsement.issuername }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.endorsement.issueremail">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.issueremail' | translate}}</p>
                        <p>
                            <a href="mailto:{{badge.endorsement.issueremail}}" core-link auto-login="no" [showBrowserWarning]="false">
                                {{ badge.endorsement.issueremail }}
                            </a>
                        </p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.endorsement.issuerurl">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.issuerurl' | translate}}</p>
                        <p><a [href]="badge.endorsement.issuerurl" core-link auto-login="no"> {{ badge.endorsement.issuerurl }} </a></p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.endorsement.dateissued">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.dateawarded' | translate}}</p>
                        <p>{{ badge.endorsement.dateissued * 1000 | coreFormatDate }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.endorsement.claimid">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.claimid' | translate}}</p>
                        <p><a [href]="badge.endorsement.claimid" core-link auto-login="no"> {{ badge.endorsement.claimid }} </a></p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.endorsement.claimcomment">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.claimcomment' | translate}}</p>
                        <p>{{ badge.endorsement.claimcomment }}</p>
                    </ion-label>
                </ion-item>
            </ion-item-group>

            <!-- Related badges -->
            <ion-item-group *ngIf="badge.relatedbadges">
                <ion-item-divider>
                    <ion-label>
                        <h2>{{ 'addon.badges.relatedbages' | translate}}</h2>
                    </ion-label>
                </ion-item-divider>
                <ion-item class="ion-text-wrap" *ngFor="let relatedBadge of badge.relatedbadges">
                    <ion-label>
                        <p class="item-heading">{{ relatedBadge.name }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.relatedbadges.length === 0">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.norelated' | translate}}</p>
                    </ion-label>
                </ion-item>
            </ion-item-group>

            <!-- Competencies alignment -->
            <ion-item-group *ngIf="badge.alignment">
                <ion-item-divider>
                    <ion-label>
                        <h2>{{ 'addon.badges.alignment' | translate}}</h2>
                    </ion-label>
                </ion-item-divider>
                <ion-item class="ion-text-wrap" *ngFor="let alignment of badge.alignment" [href]="alignment.targeturl" core-link
                    auto-login="no">
                    <ion-label>
                        <p class="item-heading">{{ alignment.targetname }}</p>
                    </ion-label>
                </ion-item>
                <ion-item class="ion-text-wrap" *ngIf="badge.alignment.length === 0">
                    <ion-label>
                        <p class="item-heading">{{ 'addon.badges.noalignment' | translate}}</p>
                    </ion-label>
                </ion-item>
            </ion-item-group>
        </ng-container>
    </core-loading>
</ion-content>
